operation.add:
			$('#uberslider-product div').live('click', function() {
				$this = $(this);
				
				if($this.hasClass('marked'))
					$(this).removeClass('marked');
				else
					$(this).addClass('marked');
			});

			function moveMarkedItemsUp() {
				$('.marked').each(function(index) {
					$this = $(this);
					$this.prev().before($this);
				});
				
				reColorThenReMap();
			}

			jQuery.fn.reverse = function() {
				return this.pushStack(this.get().reverse(), arguments);
			};

			function moveMarkedItemsDown() {
				$('.marked').reverse().each(function(index) {
					$this = $(this);
					$this.next().after($this);
				});
				
				reColorThenReMap();
			}

			function reColorThenReMap()
			{
				$('#uberslider-product div').removeClass('odd').removeClass('even');
				$('#uberslider-product div:odd').addClass('odd');
				$('#uberslider-product div:even').addClass('even');

				data = $.map($('#uberslider-product input'), function(element){
					return $(element).attr('value');
				});
								
				$('input[name=\'uberslider_product\']').attr('value', data.join());	
			}
